sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {

	var layoutFieldGenerator = new global.NotificationLayoutFieldGenerator();
	var description = layoutFieldGenerator.layoutField(current.sys_class_name, current.sys_id, "name");

	var deepLinkGenerator = new global.MobileDeepLinkGenerator("request");
	var link = deepLinkGenerator.getFormScreenLink("ad66bf93977895107846baafe153af10", current.getTableName(), current.getValue("sys_id"));

	var json = {};

	json = {
		"aps" : {
			"sound" : "default"
		},
		"Link": link,
		"Layout" : {
			"Description" : description
		}
	};

	return json;

})(current, message, attributes);
