sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(parm_input, parm_variable, actionResult) {

	var accident = new GlideRecord('x_snc_lease_mana_0_accident');

	accident.initialize();
	accident.setValue('description', parm_input.description);
	accident.setValue('location', parm_input.location);
	accident.setValue('date', parm_input.date);
	accident.setValue('driver', parm_input.driver);
	accident.setValue('vehicle', parm_input.vehicle);
	accident.setValue('reported_from_mobile', true);

	var accidentID = accident.insert();

	//gs.info('MS Mobile - Description: ' + parm_input.description);
	//gs.info('MS Mobile - Location: ' + parm_input.location);
	//gs.info('MS Mobile - Date: ' + parm_input.date);
	//gs.info('MS Mobile - Driver: ' + parm_input.driver);
	//gs.info('MS Mobile - Accident ID: ' + accidentID);
	gs.info('ms test: ' + parm_input.vehicle);

	actionResult.addAttachment("attach", "x_snc_lease_mana_0_accident", accident.getUniqueValue());  

})(parm_input, parm_variable, actionResult);
